import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import FoodRecognition from './components/FoodRecognition/FoodRecognition';
import RecValue from './components/RecValue/RecValue';
import SignIn from './components/SignIn/SignIn';
import Register from './components/Register/Register';


import './App.css';

const app = new Clarifai.App({
  apiKey: 'f695e6c1fc2d477598865bd7e8f70a4d'
 });

const particleOptions = {
  particles: {
    number: {
      value: 250,
      density: {
        enable: true,
        value_area: 800, 

      }
    }
  }
}

class App extends Component {
  constructor() {
    super ();
    this.state = {
      input: '',
      imageUrl:'',
      concepts: [],
      route: 'signin',
      isSignedIn: 'false'
    }
  }

onInputChange = (event) => {
    this.setState({input: event.target.value});
}

onButtonSubmit = () => {
  this.setState({imageUrl: this.state.input});


  app.models
  .predict(
    Clarifai.FOOD_MODEL,
   this.state.input)
   .then(response => {
    this.setState({concepts: response.outputs[0].data.concepts});
    }
  );
}

  onRouteChange = (route) => {
    if(route === 'signout') {
      this.setState({isSignedIn: false})
    } else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
     this.setState({route: route});
}

  render() {
    return (
      <div className="App">

        <Particles  className='particles'
                params={particleOptions} 
        />


      <Navigation onRouteChange = {this.onRouteChange} />
      {this.state.route === 'home' 
      ? <div>
        <Logo />
        <ImageLinkForm 
        onInputChange={this.onInputChange} 
        onButtonSubmit={this.onButtonSubmit} />
        <Rank />
        <FoodRecognition imageUrl={this.state.imageUrl} />
        <RecValue concepts={this.state.concepts}
        /> 
        </div>

      : (
        this.state.route === 'signin'
      ? <SignIn onRouteChange = {this.onRouteChange}/>
      : <Register onRouteChange = {this.onRouteChange}/>
      )
    }
  </div>

    );
  }
}

export default App;
