import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import Brusher from 'brusher';
import Clarifai from 'clarifai';
import FoodRecognition from './components/FoodRecognition/FoodRecognition';
import RecValue from './components/RecValue/RecValue'

import './App.css';





const app = new Clarifai.App({
  apiKey: 'f695e6c1fc2d477598865bd7e8f70a4d'
 });


 const brusher = new Brusher({
  image: './food.jpg'
});

brusher.init();


class App extends Component {
  constructor() {
    super ();
    this.state = {
      input: '',
      imageUrl:'',
      concepts: []
    }
  }

onInputChange = (event) => {
    this.setState({input: event.target.value});
}

onButtonSubmit = () => {
  this.setState({imageUrl: this.state.input})


  app.models
  .predict(
    Clarifai.FOOD_MODEL,
   this.state.input)
   .then(response => {
    this.setState({concepts: response.outputs[0].data.concepts});
    }
  );
}

  render() {
    return (
      <div className="App">



      <Navigation />
      <Logo />
      <ImageLinkForm onInputChange={this.onInputChange} 
      onButtonSubmit={this.onButtonSubmit} />
      <Rank />
      <FoodRecognition imageUrl={this.state.imageUrl} />
      <RecValue concepts={this.state.concepts}
      />


      </div>
    );
  }
}

export default App;
