import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import RecValue from './components/RecValue/RecValue'

import './App.css';

const app = new Clarifai.App({
  apiKey: 'f695e6c1fc2d477598865bd7e8f70a4d'
 });

const particleOptions = {
  particles: {
    number: {
      value: 250,
      density: {
        enable: true,
        value_area: 800 
      }
    }
  }
}

const createItems = (concepts)=> {   
  const items = concepts.reduce((accumulator, item)=>{     
      return accumulator + `<li>${item.name}, probability: ${item.value}</li>`;   
  },"");   
  return items; 
}

class App extends Component {
  constructor(props) {
    super (props);
    this.state = {
      input: '',
      imageUrl:''
    }
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }



  onButtonSubmit = () => {
  this.setState({imageUrl: this.state.input})


 const response = app.models
  .predict(
    Clarifai.FOOD_MODEL,
   this.state.input)
  .createItems(response.outputs[0].data.concepts)
   .then(
    function(response) {

    },
    function(err) {
      // there was an error
    }
  );
}

  render() {
    return (
      <div className="App">

        <Particles  className='particles'
                params={particleOptions} 
        />

      <Navigation />
      <Logo />
      <ImageLinkForm onInputChange={this.onInputChange} 
      onButtonSubmit={this.onButtonSubmit} />
      <Rank />
      <RecValue items={this.state.items} />
      <FaceRecognition imageUrl={this.state.imageUrl} />

      </div>
    );
  }
}

export default App;
