import React from 'react';
import './RecValue.css';
import ImageLinkForm from '../ImageLinkForm/ImageLinkForm';

const RecValue = props => {
    function renderData() {
        this.state.show
        return props.concepts.map(item => (
            <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.value}</td>
            </tr>
        )
        )
    } 

    if (ImageLinkForm === true) {

        return (
            <table>
            <thead>
                <th>Ingredients</th>
                <th>Value</th>
            </thead>
            <tbody>
            {renderData()}
            </tbody>
            </table>
            
        )
    }
}

export default RecValue;
